SELECT * FROM customer_analysis;

--1 What is the total revenue generated by male vs female customers ?
SELECT gender, SUM(purchase_amount) AS revenue
FROM public.customer_analysis
GROUP BY gender;

--2 Which customers used a discount but still spent more than the average purchase amount?
SELECT customer_id, purchase_amount
FROM public.customer_analysis
WHERE discount_applied = 'Yes' AND purchase_amount > (SELECT AVG(purchase_amount) FROM customer_analysis);

--3 Which are the top 5 products with the highest average review rating ?
SELECT item_purchased AS Product, ROUND(AVG(review_rating::NUMERIC),2) AS "Average Product Rating"
FROM public.customer_analysis
GROUP BY item_purchased
ORDER BY AVG(review_rating) DESC 
LIMIT 5;

--4 Compare the average purchase amounts between Standard and Express shipping
SELECT shipping_type, ROUND(AVG(purchase_amount),2)
FROM public.customer_analysis
WHERE shipping_type IN ('Standard','Express')
GROUP BY shipping_type;

--5 Do subscribed customers spend more ? Compare average spend and total revenue between subscribers and non-subscribers.
SELECT subscription_status, COUNT(customer_id) AS total_customers,
ROUND(AVG(purchase_amount),2) AS avg_spend, 
ROUND(SUM(purchase_amount),2) AS total_revenue
FROM public.customer_analysis
GROUP BY subscription_status
ORDER BY total_revenue, avg_spend DESC;

--6 Which 5 products have the highest percentage of purchase with discount applied ?
SELECT item_purchased, 
ROUND(100 * SUM(CASE WHEN discount_applied = 'Yes' THEN 1 ELSE 0 END)/COUNT(*),2) AS discount_rate
FROM public.customer_analysis
GROUP BY item_purchased
ORDER BY discount_rate DESC
LIMIT 5;

--7 Segment customers into New, Returning, and Loyal based on their total
--number of previous purchases, and show the count of each segment.
WITH customer_type AS (
SELECT customer_id, previous_purchases,
CASE 
	WHEN previous_purchases = 1 THEN 'New'
	WHEN previous_purchases BETWEEN 2 AND 10 THEN 'Returning'
	ELSE 'Loyal'
	END AS customer_segment
FROM public.customer_analysis
)

SELECT customer_segment, COUNT(*) AS "Number of Customers"
FROM customer_type
GROUP BY customer_segment;

--8 What are the top 3 most purchased products within each category?
WITH item_counts AS (
SELECT category, item_purchased,
COUNT(customer_id) AS total_orders,
ROW_NUMBER() OVER(PARTITION BY category ORDER BY COUNT(customer_id) DESC) AS item_rank
FROM public.customer_analysis
GROUP BY category, item_purchased
)

SELECT item_rank, category, item_purchased, total_orders
FROM item_counts
WHERE item_rank <= 3;

--9 What are the Top 3 purchased products by season ? 
WITH item_counts AS (
SELECT season, item_purchased,
COUNT(customer_id) AS total_orders,
SUM(purchase_amount) AS total_revenue,
ROW_NUMBER() OVER(PARTITION BY season ORDER BY COUNT(customer_id) DESC) AS item_rank
FROM public.customer_analysis
GROUP BY season, item_purchased
)

SELECT item_rank, season, item_purchased, total_orders, total_revenue
FROM item_counts
WHERE item_rank <= 3;

--10 Are customers who are repeat buyers (more than 5 previous purchases) also likely to subscribe?
SELECT subscription_status,
COUNT(customer_id) AS repeat_buyers
FROM public.customer_analysis
WHERE previous_purchases > 5
GROUP BY subscription_status;

--11 What is the revenue contribution of each age group ? 
SELECT age_group,
SUM(purchase_amount) AS total_revenue
FROM public.customer_analysis
GROUP BY age_group
ORDER BY total_revenue DESC;
